import React from "react";
import { Link, Route, BrowserRouter as Router, Routes } from "react-router-dom";
import About from "./pages/About";
import Blogs from "./pages/blog/Blog";
import Home from "./pages/Home";
import Resume from "./pages/Resume";
import Video from "./pages/Video";
import Writing from "./pages/Writing";
import { styles } from "./layout/layout"; // Import styles from Layout

const App: React.FC = () => {
  const [isProjectsOpen, setIsProjectsOpen] = React.useState(false);

  const menuItems = [
    { path: "/", label: "Home" },
    { path: "/about", label: "About" },
    { path: "/resume", label: "Resume" },
  ];

  const projectItems = [
    { path: "/blogs", label: "Blogs" },
    { path: "/video", label: "Video" },
    { path: "/writing", label: "Writing" },
  ];

  return (
    <Router>
      <div style={styles.mainContainer}>
        <header style={styles.header}>
          {/* Use styles from layout.tsx for the title */}
          <h1 style={styles.title}>Joe Pearson</h1>
          <nav style={styles.nav}>
            {menuItems.map((item) => (
              <Link key={item.path} to={item.path} style={styles.link}>
                {item.label}
              </Link>
            ))}

            <div
              style={styles.dropdownContainer}
              onMouseEnter={() => setIsProjectsOpen(true)}
              onMouseLeave={() => setIsProjectsOpen(false)}
            >
              <button style={styles.dropdownButton}>Projects â–¾</button>
              <div style={styles.dropdownContent}>
                {projectItems.map((item) => (
                  <Link
                    key={item.path}
                    to={item.path}
                    style={styles.dropdownLink}
                  >
                    {item.label}
                  </Link>
                ))}
              </div>
            </div>
          </nav>
        </header>

        <main style={styles.main}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/blogs" element={<Blogs />} />
            <Route path="/about" element={<About />} />
            <Route path="/resume" element={<Resume />} />
            <Route path="/writing" element={<Writing />} />
            <Route path="/video" element={<Video />} />
            <Route path="/writing/*" element={<Writing />} />
            <Route path="/blog/*" element={<Blogs />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
};

export default App;
