import React from "react";

const styles = {
  pageContainer: {
    padding: "20px",
    maxWidth: "800px",
    margin: "0 auto",
    fontFamily: "'EB Garamond', serif", // Updated to EB Garamond
  },
  header: {
    fontSize: "28px",
    marginBottom: "25px",
    fontWeight: "Bold",
    color: "#000",
  },
  subHeader: {
    fontSize: "22px",
    marginTop: "25px",
    marginBottom: "15px",
    fontWeight: "bold",
    color: "#000",
  },
  paragraph: {
    fontSize: "16px",
    lineHeight: "1.6",
    marginBottom: "20px",
  },
  list: {
    marginLeft: "20px",
    marginBottom: "20px",
  },
  listItem: {
    marginBottom: "8px",
    fontSize: "16px",
  },
  link: {
    color: "#0066cc",
    textDecoration: "none",
  },
  title: {
    fontSize: "32px",
    fontWeight: "bold" as const,
    marginBottom: "10px",
  },
  subTitle: {
    fontSize: "24px",
    fontWeight: "bold" as const,
    marginTop: "30px",
    marginBottom: "10px",
  },
  note: {
    backgroundColor: "#f0f0f0",
    border: "1px solid #ddd",
    borderRadius: "4px",
    padding: "10px",
    marginBottom: "20px",
  },
  nav: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexWrap: "wrap" as const,
    gap: "15px",
  },
  dropdownContainer: {
    position: "relative" as const,
    display: "inline-block",
  },
  dropdownButton: {
    background: "none",
    border: "none",
    color: "black",
    fontSize: "16px",
    cursor: "pointer",
    padding: "5px",
    fontFamily: '"Times New Roman", Times, serif',
  },
  dropdownContent: {
    display: isProjectsOpen ? "block" : "none",
    position: "absolute" as const,
    backgroundColor: "white",
    minWidth: "140px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    zIndex: 1,
    top: "100%",
    left: "50%",
    transform: "translateX(-50%)",
    borderRadius: "4px",
  },
  dropdownLink: {
    color: "black",
    textDecoration: "none",
    padding: "8px 12px",
    display: "block",
    textAlign: "center" as const,
  },
  mainContainer: {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column" as const,
    fontFamily: '"Times New Roman", Times, serif',
  },
  main: {
    flex: 1,
    padding: "20px",
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    backgroundColor: "white",
  },
};

interface PageLayoutProps {
  title: string;
  children: React.ReactNode;
}

const Layout: React.FC<PageLayoutProps> = ({ title, children }) => (
  <div style={styles.pageContainer}>
    <h1 style={styles.title}>{title}</h1> {/* Using title style from layout.tsx */}
    {children}
  </div>
);

export { Layout, styles };
